--
-- Create procedure `Proc_PaymentDetail_GetByPaymentIdAndPaging`
--
CREATE DEFINER = 'root'@'localhost'
PROCEDURE Proc_PaymentDetail_GetByPaymentIdAndPaging (IN p_PageSize int, IN p_PageNumber int, OUT p_TotalRecord int, IN p_PaymentId char(36))
COMMENT 'Lấy tất cả payment detail theo id payment và phân trang'
BEGIN
  SET @PageIndex = p_PageNumber;
  SET @PageSize = p_PageSize;

  CREATE TEMPORARY TABLE IF NOT EXISTS tbPaymentDetailOrginal AS
  SELECT
    ROW_NUMBER() OVER (
    ORDER BY t.ModifiedDate DESC) AS RowIndex,
    t.*,
    o.ObjectName,
    o.ObjectCode
  FROM (SELECT
      p.PaymentId,
      p.ObjectId,
      p.Amount,
      a1.AccountNumber AS DebitAccountNumber,
      a.AccountNumber AS CreditAccountNumber,
      p.Description,
      p.CreatedDate,
      p.CreatedBy,
      p.ModifiedDate,
      p.ModifiedBy
    FROM paymentdetail p
      INNER JOIN account a
        ON p.CreditAccount = a.AccountId
      INNER JOIN account a1
        ON p.DebitAccount = a1.AccountId

    WHERE p.PaymentId = p_PaymentId) AS t
    LEFT JOIN Mobject o
      ON t.ObjectId = o.ObjectId
  ;
  SET @IndexEnd = p_PageNumber * p_PageSize;
  SET @IndexStart = @IndexEnd - p_PageSize + 1;
  SET p_TotalRecord = (SELECT
      COUNT(*)
    FROM tbPaymentDetailOrginal);
  SELECT
    *
  FROM tbPaymentDetailOrginal pdo
  WHERE pdo.RowIndex BETWEEN @IndexStart AND @IndexEnd;
  DROP TEMPORARY TABLE IF EXISTS tbPaymentDetailOrginal;
END
$$

