  CREATE TEMPORARY TABLE IF NOT EXISTS tbPaymentOrginal AS
  SELECT
    ROW_NUMBER() OVER (
    ORDER BY p.ModifiedDate DESC) AS RowIndex,
    p.PaymentId,
    p.ObjectId,
    p.Reciver,
