    ROW_NUMBER() OVER (
    ORDER BY a.AccountNumber ASC) AS RowIndex,
    a.AccountId,
    a.ParentId,
    a.AccountNumber,
    a.AccountName,
    a.EnglishName,
    a.Type,
    CASE WHEN a.Type = 0 THEN 'Dư Nợ' WHEN a.Type = 1 THEN 'Dư có' WHEN a.Type = 2 THEN 'Lưỡng tính' WHEN a.Type = 3 THEN 'Không có số dư' END AS TypeName,
    a.Description,
    a.HasForeignCurrencyAccounting,
    a.IsActive,
    CASE WHEN a.IsActive = TRUE THEN 'Đang sử dụng' ELSE 'Ngừng sử dụng' END AS `Active`,
    a.IsParent,
    a.IsTrackObject,
    a.IsTrackJob,
    a.IsTrackOrder,
    a.IsTrackPurchaseContract,
    a.IsTrackOrganizationUnit,
    a.IsTrackBankAccount,
    a.IsTrackProjectWork,
    a.IsTrackSaleContract,
    a.IsTrackExpenseItem,
    a.IsTrackItem,
    a.Object,
    a.Job,
    a.`Order`,
    a.PurchaseContract,
    a.Unit,
    a.BankAccount,
    a.ProjectWork,
    a.SaleContract,
    a.ExpenseItem,
    a.Item,
    a.CreatedDate,
    a.CreatedBy,
    a.ModifiedDate,
    a.ModifiedBy,
    a.Grade
  FROM account a
  -- LEFT JOIN account p ON a.ParentId = p.AccountId
  WHERE (a.AccountNumber LIKE @Keyword
  OR a.AccountName LIKE @Keyword
  OR p_TextSearch = ''
  OR p_TextSearch IS NULL
  )
  AND a.Grade = 1;

  SET @IndexEnd = @PageIndex * @PageSize;
  SET @IndexStart = @IndexEnd - @PageSize + 1;
  SET @TotalValue = 0;
  SELECT
    *
  FROM tbAccountOrginal eo
  WHERE eo.RowIndex BETWEEN @IndexStart AND @IndexEnd;
  SET p_TotalRecord = (SELECT
      COUNT(*)
    FROM tbAccountOrginal);


  DROP TEMPORARY TABLE IF EXISTS tbAccountOrginal;
END
$$

