PROCEDURE Proc_Account_CheckDuplicate (IN p_AccountId char(36), IN p_AccountNumber varchar(20))
COMMENT 'Check trùng mã nhân viên'
BEGIN
  SELECT
    *
  FROM account a
  WHERE (p_AccountId IS NULL
  OR a.AccountId <> p_AccountId)
  && a.AccountNumber = p_AccountNumber;
END
$$

